import React, { Component } from 'react';
import { Button, ButtonGroup, Table } from 'reactstrap';
import { Container, Row, Col, Nav, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
class ProjectList extends Component {

    constructor(props) {
        super(props);
        this.state = { projects: [] };
        this.remove = this.remove.bind(this);
    }

    componentDidMount() {
        fetch('/projects')
            .then(response => response.json())
            .then(data => this.setState({ projects: data }));
    }
    async remove(id) {
        await fetch(`/projects/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedProjects = [...this.state.projects].filter(i => i.id !== id);
            this.setState({ projects: updatedProjects });
        });
    }
    render() {
        const { projects, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        const projectList = projects.map(project => {
            return <tr key={project.id}>
                <td style={{ whiteSpace: 'nowrap' }}>{project.name}</td>
                <td>{project.email}</td>
                <td>
                    <ButtonGroup>
                        <Button size="sm" color="primary" tag={Link} to={"/projects/" + project.id}>Edit</Button>
                        <Button size="sm" color="danger" onClick={() => this.remove(project.id)}>Delete</Button>
                    </ButtonGroup>
                </td>
            </tr>
        });

        return (
            <div>
            <Row className='input-wrapper'>
                <Col xl={4}>
                    <FormControl
                        value={searchField}
                        placeholder={t('form.project_number') + ', ' + t('form.project_name') + ', ' + t('form.customer')}
                        maxLength="10"
                        onChange={(event) => {
                            setSearchField(event.target.value);
                        }}/>
                </Col>
                <Col xl={3}>
                    <Select
                        ref={ref => {
                            selectRef = ref;
                        }}
                        value={searchStatus ? statuses.filter(option => option.value === searchStatus) : ''}
                        className="basic-single"
                        classNamePrefix="select"
                        options={statuses}
                        placeholder={t('form.status')}
                        onChange={(selected) => {
                            if (!selected) {
                                selected = {
                                    value: '',
                                };
                            }
                            setSearchStatus(selected.value);
                        }}
                        isClearable={true}/>
                </Col>

                <Col xl={3}>
                    <Button onClick={searchHandler}>{t('actions.search_project')}</Button>
                </Col>
                <Col xl={2}>
                    <Button variant='link' href="#" onClick={resetSearchHandler}>{t('actions.reset_search')}</Button>
                </Col>
            </Row>
            <Table bordered>
                <thead>
                <tr>
                    <th>{}</th>
                    <th>{t('table.number')}</th>
                    <th>{t('table.name')}</th>
                    <th>{t('form.status')}</th>
                    <th>{t('form.customer')}</th>
                    <th>{t('form.start_date')}</th>
                    <th>{t('actions.delete')}</th>
                </tr>
                </thead>
                <tbody>
                {emptySearchResultMessage ? <tr>
                    <td colSpan={7}>{emptySearchResultMessage}</td>
                </tr> : projects.map(project => (
                    <tr className='noBorder' key={project.id} style={{height:'65px'}}>
                        <td style={{textAlign: 'center'}}><FormCheck disabled={project.status !== 'NEW'}
                                                                     onChange={(event) => {
                                                                         changeDelList(event, project.id)
                                                                     }}/></td>
                        <td style={{paddingLeft:'30px',paddingRight:'-30px'}}>< Link to={`/projects/edit/${project.id}`}
                                  state={{project: project, isNew: false}}>{project.projectNumber}</Link></td>
                        <td>{project.name}</td>
                        <td>{statuses.length?getStatusLabel(project.status):''}</td>
                        <td>{project.customer}</td>
                        <td>{moment(Date.parse(project.startDate)).format("DD.MM.yyyy")}</td>
                        <td style={{textAlign: 'center',flexFlow:'row-reverse'}}>
                            {project.status === 'NEW' ? <Button variant='link' onClick={() => {
                                deleteProject(project.id);
                            }}>
                                <Icon.TrashFill color='red'/>
                            </Button> : ''}
                        </td>
                    </tr>))}
                <tr>{}</tr>
                </tbody>
                {deleteList.length?<tfoot>
                <tr className='noBorder' style={{display: `${count ? 'table-row' : 'none'}`, background: '#f5f9ff'}}>
                    <td colSpan={4} className='noRightBorder'>
                        <span>{deleteList.length} item(s) selected</span>
                    </td>
                    <td colSpan={3} style={{textAlign: 'center'}}><span
                        style={{float: 'right', marginRight: '25px', marginBottom: '0'}}>delete selected
                        items<Button variant='link' onClick={deleteMultipleProject}>
                            <Icon.TrashFill color='red'/>
                        </Button></span></td>
                </tr>
                <tr>{}</tr>
                </tfoot>:''}

            </Table>
        </div>
        );
    }
}
export default ProjectList;
