import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom';
import ProjectService from '../services/ProjectService'
import { Container, Row, Col, Nav, Form } from 'react-bootstrap';
import { Button, FormGroup, Input, Label } from 'reactstrap';

const AddProjectComponent = () => {

    const [number, setNumber] = useState('')
    const [name, setName] = useState('')
    const [customer, setCustomer] = useState('')
    const [groupId, setGroupId] = useState('')
    const [members, setMembers] = useState('')
    const [status, setStatus] = useState('')
    const [startDate, setStartDate] = useState('')
    const [endDate, setEndDate] = useState('')

    const navigate = useNavigate();
    const { id } = useParams();
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
      const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }
      setValidated(true);
      saveOrUpdateProject(event);
    };

    const saveOrUpdateProject = (e) => {
        e.preventDefault();

        const project = { number, name, customer, groupId, members, status, startDate, endDate }

        if (id) {
            ProjectService.updateProject(id, project).then((response) => {
                navigate('/projects')
            }).catch(error => {
                console.log(error)
            })

        } else {
            ProjectService.createProject(project).then((response) => {

                console.log(response.data)

                navigate('/projects');

            }).catch(error => {
                console.log(error)
            })
        }

    }

     useEffect(() => { 
         
        ProjectService.getProjectById(id).then((response) => {
            setNumber(response.data.number)
            setName(response.data.name)
            setCustomer(response.data.customer)
            setGroupId(response.data.groupId)
            setMembers(response.data.members)
            setStatus(response.data.status)
            setStartDate(response.data.startDate)
            setEndDate(response.data.endDate)
            alert(response)
        }).catch(error => {
            console.log(error)
        })
        

    }, [])
    
    const title = <h2>{id!='new' ? 'Edit Project information' : 'New Project'}</h2>;
    
    // const title = () => {
    //     alert(id)
    //     if (id!='new') {
    //         return <h2 className="text-center">Edit Project information</h2>
    //     } else {
    //         return <h2 className="text-center">New Project</h2>
    //     }
    // }

    return (
        <div>
            <div className="content project">
                <Container fluid>
                    <Row>
                        <Col xl={11}>
                            <p className="name-project">
                                <h1>{title}</h1>
                            </p>
                        </Col>
                    </Row>
                    <hr></hr>
                    <Form noValidate validated={validated} onSubmit={handleSubmit}>
                        <Row>
                            <div className="container">
                                <div class="row form-group">
                                    <div class="col-md-2 template required">Project Number</div>
                                    <div class="col-md-3">
                                        <input type="text reset" class="form-control" disabled={id!=='new'?true:false} value={number}
                                            onChange={(e) => setNumber(e.target.value)} required></input>
                                        <div class="valid-feedback">Valid.</div>
                                        <div class="invalid-feedback">Please fill out this field.</div>
                                    </div>
                                </div>
                                <div class="row form-group ">
                                    <div class="col-md-2 template required">Project name</div>
                                    <div class="col-md-9">
                                        <input type="text" class="form-control" value={id!=='new'?name:''}
                                            onChange={(e) => setName(e.target.value)} required></input>
                                        <div class="valid-feedback">Valid.</div>
                                        <div class="invalid-feedback">Please fill out this field.</div>
                                    </div>
                                </div>
                                <div class="row form-group">
                                    <div class="col-md-2 template required">Customer</div>
                                    <div class="col-md-9">
                                        <input type="text" class="form-control" value={id!=='new'?customer:''}
                                            onChange={(e) => setCustomer(e.target.value)} required></input>
                                        <div class="valid-feedback">Valid.</div>
                                        <div class="invalid-feedback">Please fill out this field.</div>
                                    </div>
                                </div>
                                <div class="row form-group">
                                    <div class="col-md-2 template required">Group</div>
                                    <div class="col-md-3">
                                        <select name="group" id="group" class="form-control" value={id!=='new'?groupId:''}
                                            onChange={(e) => setGroupId(e.target.value)}>
                                            <option value="new">New</option>
                                        </select>
                                    </div>
                                </div>
                                <div class="row form-group">
                                    <div class="col-md-2 template ">Members</div>
                                    <div class="col-md-9">
                                        <input type="text" class="form-control" value={id!=='new'?members:''}
                                            onChange={(e) => setMembers(e.target.value)}></input>
                                    </div>
                                </div>
                                <div class="row form-group">
                                    <div class="col-md-2 template required">Status</div>
                                    <div class="col-md-3">
                                        <select name="status" id="status" class="form-control" disabled={id!=='new'?false:true} value={id!=='new'?status:'NEW'}
                                            onChange={(e) => setStatus(e.target.value)}>
                                            <option value="NEW">New</option>
                                            <option value="PLA">Planned</option>
                                            <option value="INP">In progress</option>
                                            <option value="FIN">Finished</option>
                                        </select>
                                    </div>
                                </div>
                                <div class="row form-group">
                                    <div class="col-md-2 template required">Start date</div>
                                    <div class="col-md-3"><input type="date"  class="form-control" value={id!=='new'?startDate:''}
                                        onChange={(e) => setStartDate(e.target.value)} required></input></div>
                                    <div class="col-md-1"></div>
                                    <div class="col-md-2 template ">End date</div>
                                    <div class="col-md-3"><input type="date" class="form-control" value={id!=='new'?endDate:''}
                                        onChange={(e) => setEndDate(e.target.value)}></input></div>
                                </div>
                            </div>
                        </Row>
                        <hr></hr>
                        <Row>
                            <div class="container">
